<?php/* Copyright (c) 1998-2013 ILIAS open source, Extended GPL, see docs/LICENSE *//*** GUI class ilSCORMOfflineMode2GUI** GUI class for scorm offline player connection** @author Stefan Schneider <schneider@hrz.uni-marburg.de>* @version $Id: class.ilSCORMOfflineMode2GUI.php  $***/class ilSCORMOfflineMode2GUI{		function ilSCORMOfflineMode2GUI($type) {		global $ilias, $tpl, $lng, $ilCtrl;		//include_once "./Modules/ScormAicc/classes/class.ilSCORMOfflineMode2.php";		$this->ilias =& $ilias;		$this->tpl =& $tpl;		$lng->loadLanguageModule("sop");		$this->lng =& $lng;		$this->ctrl =& $ilCtrl;		$this->ctrl->saveParameter($this, "ref_id");		//$this->offlineMode = new ilSCORMOfflineMode2();	}		function executeCommand()	{		global $tpl, $ilCtrl;		$this->lmId = ilObject::_lookupObjectId($_GET["ref_id"]);		//$this->clientIdSop = $this->offlineMode->getClientIdSop();		$cmd = $ilCtrl->getCmd();		$this->setOfflineModeTabs($cmd);		$this->view();	}		function view() {		global $tpl;		$tpl->addBlockFile("ADM_CONTENT", "adm_content", "tpl.scorm_offline_mode2.html", "Modules/ScormAicc");		$tpl->setCurrentBlock('offline_content');		$tpl->setVariable("Test","Test");		$tpl->parseCurrentBlock();		$tpl->show();	}		function setOfflineModeTabs($a_active)	{			global $ilTabs, $ilLocator,$tpl;		//$icon = ($this->offlineMode->getOfflineMode() == "online") ? "icon_slm.svg" : "icon_slm_offline.svg";		$tabTitle = $this->lng->txt("offline_mode");		$thisurl =$this->ctrl->getLinkTarget($this, $a_active);		$ilTabs->addTab($a_active, $tabTitle, $thisurl);		$ilTabs->activateTab($a_active);		$tpl->getStandardTemplate();		$tpl->setTitle(ilObject::_lookupTitle($this->lmId));		//$tpl->setTitleIcon(ilUtil::getImagePath($icon));		$ilLocator->addRepositoryItems();		$ilLocator->addItem(ilObject::_lookupTitle($this->lmId),$thisurl);		$tpl->setLocator();	}}?>